let currentEndpoint = "items"; async function loadData(t) { currentEndpoint = t; try { let a = await fetch(`https://952540bf85e3332e.mokky.dev/${t}`), e = await a.json(); console.log(e), displayCards(e) } catch (s) { console.error("Произошла ошибка:", s) } } function displayCards(t) {
    let a = document.getElementById("cards-container"); a.innerHTML = "", t.forEach(t => {
        let e = document.createElement("div"); e.className = "col-md-3 col-sm-6 mb-4", e.innerHTML = `
    <div class="card h-100">
        <div class="card-body">
             <img src="${t.img}" alt="">
            <h5 class="card-title">${t.name}</h5>
            <p class="card-text"><strong>Описание:</strong> ${t.description}</p>
            <p class="card-text"><strong>Цена:</strong> ${t.price}</p>
        </div>
    </div>
`, a.appendChild(e)
    })
} window.onload = () => loadData("items");